```kotlin
var textFieldValue by rememberSaveable { mutableStateOf("グループ名を入力") }
TextField(
    value = textFieldValue,
    onValueChange = {
        textFieldValue = it
    },
    modifier = Modifier
        .wrapContentSize()
        .onFocusChanged {
